@tailwind base;
@tailwind components;
@tailwind utilities;

/* Custom animations for language learning background */
@keyframes spin-slow {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

@keyframes spin-reverse {
  from {
    transform: rotate(360deg);
  }
  to {
    transform: rotate(0deg);
  }
}

@keyframes slide-right {
  0% {
    transform: translateX(-100px);
    opacity: 0;
  }
  50% {
    opacity: 1;
  }
  100% {
    transform: translateX(calc(100vw + 100px));
    opacity: 0;
  }
}

@keyframes slide-left {
  0% {
    transform: translateX(100px);
    opacity: 0;
  }
  50% {
    opacity: 1;
  }
  100% {
    transform: translateX(calc(-100vw - 100px));
    opacity: 0;
  }
}

@keyframes float {
  0%, 100% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-20px);
  }
}

@keyframes pulse-slow {
  0%, 100% {
    opacity: 0.3;
    transform: scale(1);
  }
  50% {
    opacity: 0.8;
    transform: scale(1.1);
  }
}

@keyframes flag-transform {
  0%, 80% {
    transform: rotate(0deg) scale(1);
  }
  90% {
    transform: rotate(180deg) scale(0.8);
  }
  100% {
    transform: rotate(360deg) scale(1);
  }
}

.animate-spin-slow {
  animation: spin-slow 25s linear infinite;
}

.animate-spin-reverse {
  animation: spin-reverse 20s linear infinite;
}

.border-3 {
  border-width: 3px;
}

.animate-slide-right {
  animation: slide-right 12s linear infinite;
}

.animate-slide-left {
  animation: slide-left 14s linear infinite;
}

.animate-slide-right-delayed {
  animation: slide-right 16s linear infinite;
  animation-delay: 4s;
}

.animate-float {
  animation: float 6s ease-in-out infinite;
}

.animate-float-delayed {
  animation: float 6s ease-in-out infinite;
  animation-delay: 2s;
}

.animate-float-slow {
  animation: float 8s ease-in-out infinite;
  animation-delay: 1s;
}

.animate-pulse-slow {
  animation: pulse-slow 4s ease-in-out infinite;
}

.animate-pulse-slow-delayed {
  animation: pulse-slow 4s ease-in-out infinite;
  animation-delay: 2s;
}

.animate-flag-transform {
  animation: flag-transform 6s ease-in-out infinite;
}

:root {
