                        <p className="text-sm text-red-500 mt-1">{errors.firstName.message}</p>
                      )}
                    </div>

                    <div>
                      <Label htmlFor="lastName">Last Name</Label>
                      <Input
                        id="lastName"
                        {...register("lastName")}
                        disabled={!isEditing}
                        className="mt-1"
                      />
                      {errors.lastName && (
                        <p className="text-sm text-red-500 mt-1">{errors.lastName.message}</p>
                      )}
                    </div>
                  </div>

                  <div>
                    <Label htmlFor="location">Location</Label>
                    <Input
                      id="location"
                      {...register("location")}
                      disabled={!isEditing}
                      className="mt-1"
                      placeholder="e.g., New York, USA"
                    />
                  </div>

                  <div>
                    <Label>Field of Learning/Work</Label>
                    <Select
                      onValueChange={(value) => setValue("fieldOfLearning", value)}
                      disabled={!isEditing}
                    >
                      <SelectTrigger className="mt-1">
                        <SelectValue placeholder={user.fieldOfLearning || "Select your field"} />
                      </SelectTrigger>
                      <SelectContent>
                        {FIELD_OPTIONS.map((field) => (
                          <SelectItem key={field} value={field}>
                            {field}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>

                  {isEditing && (
                    <div>
                      <Label>Interests</Label>
                      <div className="mt-2 flex flex-wrap gap-2">
                        {INTEREST_OPTIONS.map((interest) => (
                          <Badge
                            key={interest}
                            variant={selectedInterests.includes(interest) ? "default" : "outline"}
                            className="cursor-pointer hover:bg-blue-100 dark:hover:bg-blue-900"
                            onClick={() => toggleInterest(interest)}
                          >
                            {interest}
                          </Badge>
                        ))}
                      </div>
                    </div>
                  )}

                  <div>
                    <Label>Current Language Level</Label>
                    <Select
                      onValueChange={(value) => setValue("cefr_level", value as any)}
                      disabled={!isEditing}
                    >
                      <SelectTrigger className="mt-1">
                        <SelectValue placeholder={user.cefr_level || "Select your level"} />
                      </SelectTrigger>
                      <SelectContent>
                        {CEFR_LEVELS.map((level) => (
                          <SelectItem key={level.value} value={level.value}>
                            <div>
                              <div className="font-medium">{level.label}</div>
                              <div className="text-xs text-gray-500">{level.description}</div>
                            </div>
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>

                  <div>
                    <Label htmlFor="dailyGoalMinutes">Daily Study Goal (minutes)</Label>
                    <Input
                      id="dailyGoalMinutes"
                      type="number"
                      min="5"
                      max="240"
                      {...register("dailyGoalMinutes", { valueAsNumber: true })}
                      disabled={!isEditing}
                      className="mt-1"
                    />
                  </div>

                  {isEditing && (
                    <Button
                      type="submit"
                      className="w-full"
                      disabled={updateProfileMutation.isPending}
                    >
